import * as React from 'react';
import withStyles from "@material-ui/core/styles/withStyles";
import { useQuery } from '@apollo/react-hooks';
import { useMutation } from '@apollo/react-hooks';
import gql from "graphql-tag";
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import {Typography} from "@material-ui/core";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";


const styles = theme => ({
    submitButton: {
        fontSize: 'smaller',
        borderColor: '#fff',
        color: '#fff',
        background: '#550055',
        height: 60,
        justifyContent: 'center',
        marginTop: 80,
        width: '100%',
        '&:hover': {
            background: '#310131',
        },
    },
    container: {
        display: 'flex',
        flexDirection: 'column',
        maxWidth: 400,
        height: 600,
        justifyContent: 'space-around',
        margin: '0 auto',
    },
});

const addUser = gql`
    mutation ($username: String! $email: String! $telephone: Int $role: String $description: String){
        newUser(
            username: $username,
            email: $email,
            telephone: $telephone,
            role: $role,
            description: $description,
            ){
        username
        email
        telephone
        role
        description
        }
    }
`;

function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
}

function AddUser(props) {
    const {classes} = props
    const [addData, { data }] = useMutation(addUser);

    let newUserData = {
        username: '',
        email: '',
        telephone: 0,
        role: '',
        description: '',
    }

    const  onInput = (event) => {
    //     const newUserData = {...this.state.newUserData}
    //     newUserData[event.target.id] = event.currentTarget.value
    //     this.setState({newUserData})
    }


    // console.log(data);
    return (
        <div>
            <Typography>Fill in new user info:</Typography>
            <div className={classes.container}>
                <form
                    onSubmit={e => {
                        e.preventDefault();
                        addData({ variables: { type: input.value } });
                    }}
                >
                <TextField id="username" label="Username" onChange={onInput}/>
                <TextField id="email" label="Email" onChange={onInput}/>
                <TextField id="telephone" label="Telephone" onChange={onInput}/>
                <TextField id="role" label="Role" onChange={onInput}/>
                <TextField id="description" label="Description" onChange={onInput}/>
                </form>
            </div>
            <Button
                className={classes.submitButton}

            >
                Add User
            </Button>
        </div>
    );
}




export default (withStyles(styles))(AddUser);
