import * as React from 'react';
import withStyles from "@material-ui/core/styles/withStyles";
import Button from "@material-ui/core/Button";
import ButtonGroup from '@material-ui/core/ButtonGroup';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import TextField from '@material-ui/core/TextField';
import { useQuery } from '@apollo/react-hooks';
import GetUsers from "./components/getUsers";
import AddUser from "./components/createUsers";
import {Typography} from "@material-ui/core";

export const styles = theme => ({
    componentContainer: {
        display: 'flex',
    },
    buttonGroup: {
        width: 180,
        marginRight: 200,
    },
    button: {
        fontSize: 'smaller',
        borderColor: '#fff',
        color: '#fff',
        background: '#550055',
        height: 60,
        justifyContent: 'center',
        '&:hover': {
            background: '#310131',
        },
    },
    buttonSelected: {
        fontSize: 'smaller',
        borderColor: '#550055',
        color: '#550055',
        background: '#fff',
        height: 60,
        justifyContent: 'center',
        '&:hover': {
            background: '#acacac',
        },
    },
    container: {
        display: 'flex',
        flexDirection: 'column',
        maxWidth: 400,
        height: 600,
        justifyContent: 'space-around',
        margin: '0 auto',
    },
    card: {
        width: 800,
        height: 800,
    },
    submitButton: {
        fontSize: 'smaller',
        borderColor: '#fff',
        color: '#fff',
        background: '#550055',
        height: 60,
        justifyContent: 'center',
        marginTop: 80,
        width: '100%',
        '&:hover': {
            background: '#310131',
        },
    },
});





class GraphAPI extends React.Component {

    state = {
        currentView: 'read',
        isAddUserOpen: false,
        newUserData: {
            username: '',
            email: '',
            telephone: 0,
            role: '',
            description: '',
        },
    };

    constructor(props) {
        super(props);
    }

    componentDidMount() {
    }

    switchCurrentType = (event) => {
        this.setState({currentView: event.currentTarget.value});
    };

    openAddUser = () => {
        if (!this.state.isAddUserOpen) {
            this.setState({isAddUserOpen: true})
        } else {
            this.setState({isAddUserOpen: false})
        }
    }

    onInput = (event) => {
        // this.setState({ name : value})
        console.log(event.target.id);
        console.log(event.currentTarget.value);
    }

    render() {
        const {classes} = this.props;

        const {currentView} = this.state

        return (
            <React.Fragment>
                <div className={classes.componentContainer}>
                    <ButtonGroup
                        className={classes.buttonGroup}
                        value={currentView}
                        orientation="vertical"
                        aria-label="vertical outlined primary button group"
                    >
                        <Button
                            value={'create'}
                            classes={{
                                root: classes.button,
                                disabled: classes.buttonSelected,
                            }}
                            onClick={this.switchCurrentType}
                            disabled={currentView === 'create'}
                        >Create</Button>
                        <Button
                            value={'read'}
                            classes={{
                                root: classes.button,
                                disabled: classes.buttonSelected,
                            }}
                            onClick={this.switchCurrentType}
                            disabled={currentView === 'read'}
                        >Read</Button>
                        <Button
                            value={'update'}
                            classes={{
                                root: classes.button,
                                disabled: classes.buttonSelected,
                            }}
                            onClick={this.switchCurrentType}
                            disabled={currentView === 'update'}
                        >Update</Button>
                        <Button
                            value={'delete'}
                            classes={{
                                root: classes.button,
                                disabled: classes.buttonSelected,
                            }}
                            onClick={this.switchCurrentType}
                            disabled={currentView === 'delete'}
                        >Delete</Button>
                    </ButtonGroup>


                    <Card className={classes.card}>
                        <CardContent>
                            {currentView === "create" ?
                                <div>
                                    <Typography>Fill in new user info:</Typography>
                                    <div className={classes.container}>
                                        <TextField id="username" label="Username" onChange={this.onInput}/>
                                        <TextField id="email" label="Email" onChange={this.onInput}/>
                                        <TextField id="telephone" label="Telephone" onChange={this.onInput}/>
                                        <TextField id="role" label="Role" onChange={this.onInput}/>
                                        <TextField id="description" label="Description" onChange={this.onInput}/>
                                    </div>
                                    <Button
                                        className={classes.submitButton}
                                        onClick={this.openAddUser}
                                    >
                                        Add User
                                    </Button>
                                    {this.state.isAddUserOpen ?
                                        <AddUser
                                            newUserData={this.state.newUserData}
                                        />
                                        : null
                                    }

                                </div>
                                : null
                            }
                            {currentView === "read" ?
                                <GetUsers/>
                            : null
                            }
                            {currentView === "update" ?
                                <span>update</span>
                                : null
                            }
                            {currentView === "delete" ?
                                <span>delete</span>
                                : null
                            }
                        </CardContent>
                    </Card>


                </div>
            </React.Fragment>
        );
    }

    handleClick(data) {
        console.log(data)
    }

}

export default withStyles(styles)(GraphAPI);
